import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import axios from 'axios';

// Ensure axios has the Authorization header synchronously when a token
// is present in localStorage. This ensures components that mount immediately
// after the app starts will have authenticated requests available.
try {
  const __initial_token = typeof window !== 'undefined' ? localStorage.getItem('token') : null;
  if (__initial_token) {
    axios.defaults.headers.common['Authorization'] = `Bearer ${__initial_token}`;
  }
} catch (e) {
  // ignore in non-browser environments
}

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(() => {
    // Initialize token from localStorage synchronously on mount
    try {
      return typeof window !== 'undefined' ? localStorage.getItem('token') : null;
    } catch (e) {
      return null;
    }
  });
  const [loading, setLoading] = useState(true);

  // Simple login function
  const login = async (credentials) => {
    try {
      console.log('ðŸ” Attempting login with:', { username: credentials.username, hasPassword: !!credentials.password });
      const response = await axios.post('/api/auth/login', credentials);
      console.log('âœ… Login response:', response.data);
      const { token: newToken, user: userData } = response.data.data;
      
      setToken(newToken);
      setUser(userData);
      localStorage.setItem('token', newToken);
      localStorage.setItem('user', JSON.stringify(userData));
      
      // Set axios default header
      axios.defaults.headers.common['Authorization'] = `Bearer ${newToken}`;
      
      return { success: true, user: userData };
    } catch (error) {
      console.error('âŒ Login error details:', {
        message: error.message,
        status: error.response?.status,
        statusText: error.response?.statusText,
        data: error.response?.data,
        config: error.config
      });
      return { 
        success: false, 
        message: error.response?.data?.message || `Login failed: ${error.response?.status} ${error.response?.statusText}` 
      };
    }
  };

  // Simple logout function (memoized so effects depending on it stay stable)
  const logout = useCallback(() => {
    setUser(null);
    setToken(null);
    try {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
    } catch (e) {
      // ignore in non-browser or storage-error cases
    }
    delete axios.defaults.headers.common['Authorization'];
  }, []);

  // Load user profile
  const loadUserProfile = useCallback(async () => {
    if (!token) {
      setLoading(false);
      return;
    }

    try {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      const response = await axios.get('/api/auth/profile');
      setUser(response.data.data);
    } catch (error) {
      console.warn('Profile load failed:', error);
      logout();
    } finally {
      setLoading(false);
    }
  }, [token, logout]); // Add token and logout to dependencies

  // Check permissions
  const hasPermission = (permission) => {
    if (!user) return false;
    if (user.role === 'admin') return true;
    return user.permissions?.includes(permission) || false;
  };

  const isAdmin = () => {
    return user?.role === 'admin';
  };

  // Initialize on mount: restore user from localStorage if token exists
  useEffect(() => {
    if (token) {
      // Token is present, restore user from localStorage
      const savedUser = localStorage.getItem('user');
      if (savedUser) {
        try {
          setUser(JSON.parse(savedUser));
          axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
          console.log('âœ… Session restored from localStorage');
        } catch (error) {
          console.warn('Failed to restore user from localStorage:', error);
          logout();
          return;
        }
      }
      // Load fresh profile to ensure data is up-to-date
      loadUserProfile();
    } else {
      // No token, mark loading as done
      setLoading(false);
    }
  }, [token, loadUserProfile]);

  const value = {
    user,
    token,
    loading,
    login,
    logout,
    hasPermission,
    isAdmin,
    loadUserProfile
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
}

export default AuthContext;
