/**
 * Task Manager Component
 * Quáº£n lÃ½ cÃ´ng viá»‡c Ä‘á»‹nh ká»³ cho dá»± Ã¡n
 */

import React, { useState, useEffect } from 'react';
import { Card, Button, Badge, Table, Modal, Form, Row, Col, Tab, Nav } from 'react-bootstrap';
import TaskForm from './TaskForm';
import TaskCalendar from './TaskCalendar';
import './TaskManager.css';

const TASK_TYPES = {
  fire_safety: { label: 'PhÃ²ng chÃ¡y', icon: 'ðŸ”¥', color: 'danger' },
  security: { label: 'An ninh', icon: 'ðŸ”’', color: 'warning' },
  maintenance: { label: 'Báº£o trÃ¬', icon: 'ðŸ”§', color: 'info' },
  inspection: { label: 'Kiá»ƒm tra', icon: 'ðŸ”', color: 'primary' },
  cleaning: { label: 'Vá»‡ sinh', icon: 'ðŸ§¹', color: 'success' },
  equipment_check: { label: 'Thiáº¿t bá»‹', icon: 'âš™ï¸', color: 'secondary' },
  other: { label: 'KhÃ¡c', icon: 'ðŸ“‹', color: 'dark' }
};

const STATUS_LABELS = {
  pending: { label: 'Chá» xá»­ lÃ½', variant: 'secondary' },
  in_progress: { label: 'Äang lÃ m', variant: 'primary' },
  completed: { label: 'HoÃ n thÃ nh', variant: 'success' },
  overdue: { label: 'QuÃ¡ háº¡n', variant: 'danger' },
  cancelled: { label: 'ÄÃ£ há»§y', variant: 'dark' }
};

const PRIORITY_LABELS = {
  low: { label: 'Tháº¥p', variant: 'secondary' },
  medium: { label: 'Trung bÃ¬nh', variant: 'info' },
  high: { label: 'Cao', variant: 'warning' },
  critical: { label: 'Kháº©n cáº¥p', variant: 'danger' }
};

function TaskManager({ projectId, projectName }) {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [activeTab, setActiveTab] = useState('list');
  
  // Filters
  const [filters, setFilters] = useState({
    status: '',
    task_type: '',
    assigned_to: ''
  });

  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    in_progress: 0,
    completed: 0,
    overdue: 0
  });

  useEffect(() => {
    if (projectId) {
      loadTasks();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [projectId, filters]);

  const loadTasks = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const queryParams = new URLSearchParams(filters);
      
      const response = await fetch(`/api/projects/${projectId}/tasks?${queryParams}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      const data = await response.json();
      if (data.success) {
        setTasks(data.data.tasks || []);
        calculateStats(data.data.tasks || []);
      }
    } catch (error) {
      console.error('Error loading tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (taskList) => {
    const stats = {
      total: taskList.length,
      pending: taskList.filter(t => t.status === 'pending').length,
      in_progress: taskList.filter(t => t.status === 'in_progress').length,
      completed: taskList.filter(t => t.status === 'completed').length,
      overdue: taskList.filter(t => t.status === 'overdue').length
    };
    setStats(stats);
  };

  const handleCreateTask = () => {
    setEditingTask(null);
    setShowForm(true);
  };

  const handleEditTask = (task) => {
    setEditingTask(task);
    setShowForm(true);
  };

  const handleDeleteTask = async (taskId) => {
    if (!window.confirm('Báº¡n cÃ³ cháº¯c muá»‘n xÃ³a cÃ´ng viá»‡c nÃ y?')) return;
    
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`/api/projects/${projectId}/tasks/${taskId}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
