          </Modal.Header>
          <Modal.Body>
            <Row>
              <Col md={6}>
                <h6>ThÃ´ng tin chung</h6>
                <div className="mb-2"><strong>TiÃªu Ä‘á»:</strong> {selectedTask.task.title}</div>
                <div className="mb-2"><strong>MÃ´ táº£:</strong> {selectedTask.task.description || 'N/A'}</div>
                <div className="mb-2">
                  <strong>Loáº¡i:</strong>{' '}
                  <Badge bg={getTaskTypeInfo(selectedTask.task.task_type).color}>
                    {getTaskTypeInfo(selectedTask.task.task_type).label}
                  </Badge>
                </div>
                <div className="mb-2">
                  <strong>Æ¯u tiÃªn:</strong>{' '}
                  <Badge bg={PRIORITY_LABELS[selectedTask.task.priority]?.variant}>
