                </div>
                <div className="mb-2">
                  <strong>Tráº¡ng thÃ¡i:</strong>{' '}
                  <Badge bg={STATUS_LABELS[selectedTask.task.status]?.variant}>
                    {STATUS_LABELS[selectedTask.task.status]?.label}
                  </Badge>
                </div>
              </Col>
              <Col md={6}>
                <h6>Lá»‹ch trÃ¬nh</h6>
                <div className="mb-2"><strong>NgÃ y báº¯t Ä‘áº§u:</strong> {formatDate(selectedTask.task.start_date)}</div>
                <div className="mb-2"><strong>Háº¡n chÃ³t:</strong> {formatDate(selectedTask.task.due_date)}</div>
                <div className="mb-2"><strong>Táº§n suáº¥t:</strong> {selectedTask.task.frequency}</div>
                {selectedTask.task.is_recurring && (
                  <div className="mb-2"><strong>Láº§n káº¿ tiáº¿p:</strong> {formatDate(selectedTask.task.next_due_date)}</div>
                )}
                <div className="mb-2"><strong>NgÆ°á»i thá»±c hiá»‡n:</strong> {selectedTask.task.assigned_to_name || 'ChÆ°a gÃ¡n'}</div>
              </Col>
            </Row>

            {selectedTask.task.completed_at && (
              <div className="mt-3 pt-3 border-top">
                <h6>ThÃ´ng tin hoÃ n thÃ nh</h6>
                <div className="mb-2"><strong>HoÃ n thÃ nh lÃºc:</strong> {formatDate(selectedTask.task.completed_at)}</div>
                <div className="mb-2"><strong>NgÆ°á»i hoÃ n thÃ nh:</strong> {selectedTask.task.completed_by_name}</div>
                {selectedTask.task.completion_notes && (
                  <div className="mb-2"><strong>Ghi chÃº:</strong> {selectedTask.task.completion_notes}</div>
                )}
              </div>
            )}

            {selectedTask.comments && selectedTask.comments.length > 0 && (
              <div className="mt-3 pt-3 border-top">
                <h6>BÃ¬nh luáº­n ({selectedTask.comments.length})</h6>
                {selectedTask.comments.map(comment => (
                  <div key={comment.id} className="mb-2 p-2 bg-light rounded">
                    <small className="text-muted">{comment.user_name} - {formatDate(comment.created_at)}</small>
                    <div>{comment.comment}</div>
                  </div>
                ))}
              </div>
            )}
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setShowDetailModal(false)}>
              ÄÃ³ng
            </Button>
          </Modal.Footer>
        </Modal>
      )}
    </div>
  );
}

export default TaskManager;
