/**
 * AI ChatBot Component - KHO MVG
 * Tr·ª£ l√Ω AI chat interface
 */

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { Modal, Form, Button, Badge, Dropdown } from 'react-bootstrap';
import { useNotification } from '../../contexts/NotificationContext';
import axios from 'axios';

function ChatBot({ show, onHide }) {
  // const { user } = useAuth();
  const { showError, showSuccess } = useNotification();
  
  const [sessionId, setSessionId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [aiProvider, setAiProvider] = useState('');
  const [totalCost, setTotalCost] = useState(0);
  const [isInitializing, setIsInitializing] = useState(false);
  
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  // Auto scroll to bottom when new messages arrive
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Focus input when modal opens
  useEffect(() => {
    if (show && sessionId && inputRef.current) {
      setTimeout(() => inputRef.current?.focus(), 100);
    }
  }, [show, sessionId]);

  // Start new chat session
  const startChatSession = useCallback(async () => {
    setIsInitializing(true);
    try {
      const response = await axios.post('/api/ai/chat/start');
      const { session_id, ai_provider, ai_model, welcome_message } = response.data.data;
      
      setSessionId(session_id);
      setAiProvider(`${ai_provider} (${ai_model})`);
      setMessages([{
        role: 'assistant',
        content: welcome_message,
        timestamp: new Date().toISOString()
      }]);
      
      showSuccess('ƒê√£ k·∫øt n·ªëi v·ªõi AI Assistant');
    } catch (error) {
      console.error('Error starting chat:', error);
      const errorMsg = error.response?.data?.message || 'Kh√¥ng th·ªÉ kh·ªüi t·∫°o chat session';
      
      // Show error message in chat instead of notification
      setMessages([{
        role: 'error',
        content: `‚ö†Ô∏è ${errorMsg}\n\nVui l√≤ng:\n1. V√†o Settings ‚Üí C·∫•u h√¨nh AI\n2. Th√™m API key cho √≠t nh·∫•t 1 provider\n3. Th·ª≠ l·∫°i chat`,
        timestamp: new Date().toISOString()
      }]);
      
      // Don't block closing modal
      setSessionId('error');
    } finally {
      setIsInitializing(false);
    }
  }, [showSuccess]);

  // Start chat session when modal opens
  useEffect(() => {
    if (show && !sessionId) {
      startChatSession();
    }
  }, [show, sessionId, startChatSession]);

  // Send message to AI
  const sendMessage = async () => {
    if (!inputMessage.trim() || !sessionId || isLoading) return;

    const userMessage = inputMessage.trim();
    setInputMessage('');
    setIsLoading(true);

    // Add user message to chat
    const newUserMessage = {
      role: 'user',
      content: userMessage,
      timestamp: new Date().toISOString()
    };
    setMessages(prev => [...prev, newUserMessage]);

    try {
      const response = await axios.post('/api/ai/chat/message', {
        session_id: sessionId,
        message: userMessage
      });

      const { response: aiResponse, sql_query, query_result, cost, from_cache } = response.data.data;

      // Add AI response to chat
      const aiMessage = {
        role: 'assistant',
        content: aiResponse,
        sql_query,
        query_result,
        cost,
        from_cache,
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, aiMessage]);

      // Update total cost
      if (cost) {
        setTotalCost(prev => prev + cost);
      }

    } catch (error) {
      showError(error.response?.data?.message || 'L·ªói khi g·ª≠i tin nh·∫Øn');
      
      // Add error message to chat
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Xin l·ªói, t√¥i g·∫∑p l·ªói khi x·ª≠ l√Ω tin nh·∫Øn c·ªßa b·∫°n. Vui l√≤ng th·ª≠ l·∫°i.',
        isError: true,
        timestamp: new Date().toISOString()
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle form submit
  const handleSubmit = (e) => {
    e.preventDefault();
    sendMessage();
  };

  // End chat session
  const endChatSession = async () => {
    // Allow closing even without sessionId
    if (!sessionId || sessionId === 'error') {
      if (onHide) onHide();
      return;
    }
    
    try {
      await axios.post(`/api/ai/chat/${sessionId}/end`);
      showSuccess('ƒê√£ k·∫øt th√∫c phi√™n chat');
    } catch (error) {
      import('../../utils/errorLogger').then(({ default: ErrorLogger }) => {
        ErrorLogger.logError(error, { action: 'endChatSession', sessionId });
      });
    }
    
    // Reset state
    setSessionId(null);
    setMessages([]);
    setTotalCost(0);
    
    // Always call onHide to close modal
    if (onHide) {
      onHide();
    }
  };

  // Rate AI response (satisfaction scoring)
  const rateResponse = async (messageIndex, hasFollowUp = false) => {
    const message = messages[messageIndex];
    if (!message || message.role !== 'assistant') return;

    try {
      await axios.post('/api/ai/chat/rate', {
        session_id: sessionId,
        message_id: messageIndex, // In real app, use actual message ID from DB
        follow_up_question: hasFollowUp
      });
      
      if (hasFollowUp) {
        showSuccess('ƒê√£ ghi nh·∫≠n b·∫°n c·∫ßn h·ªó tr·ª£ th√™m');
      }
    } catch (error) {
      import('../../utils/errorLogger').then(({ default: ErrorLogger }) => {
        ErrorLogger.logError(error, { action: 'rateResponse', messageIndex, hasFollowUp });
      });
    }
  };

  // Quick action buttons
  const quickActions = [
    "C√≥ bao nhi√™u d·ª± √°n ƒëang ho·∫°t ƒë·ªông?",
    "Danh s√°ch kh√°ch h√†ng VIP",
    "H·ª£p ƒë·ªìng s·∫Øp h·∫øt h·∫°n trong 30 ng√†y",
    "T·ª∑ l·ªá thu√™ kho trung b√¨nh",
    "Doanh thu th√°ng n√†y"
  ];

  const handleQuickAction = (action) => {
    setInputMessage(action);
    setTimeout(() => sendMessage(), 100);
  };

  // Format query result for display
  const formatQueryResult = (result) => {
    if (!result || !result.data) return null;
    
    const data = result.data;
    if (Array.isArray(data) && data.length > 0) {
      return (
        <div className="query-result mt-2 p-2 bg-light rounded">
          <small className="text-muted">üìä K·∫øt qu·∫£ truy v·∫•n:</small>
          <div className="table-responsive" style={{ maxHeight: '200px' }}>
            <table className="table table-sm table-bordered">
              <thead>
                <tr>
                  {Object.keys(data[0]).map(key => (
                    <th key={key} className="small">{key}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {data.slice(0, 10).map((row, idx) => (
                  <tr key={idx}>
                    {Object.values(row).map((value, i) => (
                      <td key={i} className="small">{String(value)}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          {data.length > 10 && (
            <small className="text-muted">Hi·ªÉn th·ªã 10/{data.length} d√≤ng ƒë·∫ßu ti√™n</small>
          )}
        </div>
      );
    }
    
    return (
      <div className="query-result mt-2 p-2 bg-light rounded">
        <small className="text-muted">üìä K·∫øt qu·∫£: </small>
        <code>{JSON.stringify(data)}</code>
      </div>
    );
  };

  return (
    <Modal 
      show={show} 
      onHide={endChatSession}
      size="lg"
      backdrop="static"
      className="ai-chatbot-modal"
    >
      <Modal.Header closeButton className="bg-gradient-primary text-white">
        <Modal.Title className="d-flex align-items-center">
          <i className="fas fa-robot me-2"></i>
          Tr·ª£ l√Ω AI KHO MVG
          {aiProvider && (
            <Badge bg="light" text="dark" className="ms-2 small">
              {aiProvider}
            </Badge>
          )}
        </Modal.Title>
      </Modal.Header>

      <Modal.Body className="p-0" style={{ height: '500px' }}>
        {isInitializing ? (
          <div className="d-flex justify-content-center align-items-center h-100">
            <div className="text-center">
              <div className="spinner-border text-primary mb-3"></div>
              <p>ƒêang kh·ªüi t·∫°o AI Assistant...</p>
            </div>
          </div>
        ) : (
          <>
            {/* Chat Messages */}
            <div className="chat-messages p-3" style={{ height: '350px', overflowY: 'auto' }}>
              {messages.map((message, index) => (
                <div key={index} className={`message ${message.role}`}>
                  <div className={`message-bubble ${message.role === 'user' ? 'user-message' : 'ai-message'} ${message.isError ? 'error-message' : ''}`}>
                    <div className="message-content">
                      {message.content}
                      
                      {/* Show SQL query if available */}
                      {message.sql_query && (
                        <div className="sql-query mt-2 p-2 bg-dark text-light rounded">
                          <small className="text-warning">üîç SQL Query:</small>
                          <pre className="small mb-0">{message.sql_query}</pre>
                        </div>
                      )}
                      
                      {/* Show query result if available */}
                      {formatQueryResult(message.query_result)}
                    </div>
                    
                    <div className="message-meta d-flex justify-content-between align-items-center mt-2">
                      <small className="text-muted">
                        {new Date(message.timestamp).toLocaleTimeString('vi-VN')}
                        {message.from_cache && (
                          <Badge bg="info" className="ms-1 small">Cache</Badge>
                        )}
                        {message.cost > 0 && (
                          <Badge bg="warning" className="ms-1 small">
                            ${message.cost.toFixed(4)}
                          </Badge>
                        )}
                      </small>
                      
                      {/* Rating buttons for AI messages */}
                      {message.role === 'assistant' && !message.isError && (
                        <div className="rating-buttons">
                          <Button 
                            variant="outline-success" 
                            size="sm"
                            onClick={() => rateResponse(index, false)}
                            title="H√†i l√≤ng"
                          >
                            <i className="fas fa-thumbs-up fa-xs"></i>
                          </Button>
                          <Button 
                            variant="outline-warning" 
                            size="sm"
                            className="ms-1"
                            onClick={() => rateResponse(index, true)}
                            title="C·∫ßn h·ªó tr·ª£ th√™m"
                          >
                            <i className="fas fa-question fa-xs"></i>
                          </Button>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              ))}
              
              {isLoading && (
                <div className="message ai">
                  <div className="message-bubble ai-message">
                    <div className="typing-indicator">
                      <span></span>
                      <span></span>
                      <span></span>
                    </div>
                  </div>
                </div>
              )}
              
              <div ref={messagesEndRef} />
            </div>

            {/* Quick Actions */}
            {messages.length <= 1 && !isLoading && (
              <div className="quick-actions px-3 pb-2">
                <small className="text-muted">üí° G·ª£i √Ω c√¢u h·ªèi:</small>
                <div className="d-flex flex-wrap gap-1 mt-1">
                  {quickActions.slice(0, 3).map((action, index) => (
                    <Button
                      key={index}
                      variant="outline-primary"
                      size="sm"
                      onClick={() => handleQuickAction(action)}
                    >
                      {action}
                    </Button>
                  ))}
                  <Dropdown>
                    <Dropdown.Toggle variant="outline-secondary" size="sm">
                      Xem th√™m...
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                      {quickActions.slice(3).map((action, index) => (
                        <Dropdown.Item 
                          key={index}
                          onClick={() => handleQuickAction(action)}
                        >
                          {action}
                        </Dropdown.Item>
                      ))}
                    </Dropdown.Menu>
                  </Dropdown>
                </div>
              </div>
            )}
          </>
        )}
      </Modal.Body>

      <Modal.Footer className="border-0 p-0">
        {!isInitializing && (
          <Form onSubmit={handleSubmit} className="w-100">
            <div className="input-group">
              <Form.Control
                ref={inputRef}
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                placeholder="H·ªèi t√¥i v·ªÅ d·ªØ li·ªáu d·ª± √°n, kh√°ch h√†ng, h·ª£p ƒë·ªìng..."
                disabled={isLoading}
                className="border-0"
                style={{ borderRadius: 0 }}
              />
              <Button 
                type="submit" 
                variant="primary"
                disabled={!inputMessage.trim() || isLoading}
                style={{ borderRadius: 0 }}
              >
                {isLoading ? (
                  <div className="spinner-border spinner-border-sm"></div>
                ) : (
                  <i className="fas fa-paper-plane"></i>
                )}
              </Button>
            </div>
          </Form>
        )}
        
        {/* Cost tracking */}
        {totalCost > 0 && (
          <div className="cost-tracker position-absolute" style={{ top: '-30px', right: '15px' }}>
            <small className="text-muted">
              Chi ph√≠: <strong>${totalCost.toFixed(4)}</strong>
            </small>
          </div>
        )}
      </Modal.Footer>

      <style>{`
        .ai-chatbot-modal .modal-dialog {
          max-width: 800px;
        }

        .bg-gradient-primary {
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
        }

        .chat-messages {
          background: #f8f9fa;
        }

        .message {
          margin-bottom: 1rem;
        }

        .message.user {
          display: flex;
          justify-content: flex-end;
        }

        .message.ai {
          display: flex;
          justify-content: flex-start;
        }

        .message-bubble {
          max-width: 80%;
          padding: 12px 16px;
          border-radius: 18px;
          word-wrap: break-word;
        }

        .user-message {
          background: #007bff;
          color: white;
          margin-left: auto;
        }

        .ai-message {
          background: white;
          color: #333;
          box-shadow: 0 1px 2px rgba(0,0,0,0.1);
          margin-right: auto;
        }

        .error-message {
          background: #f8d7da;
          color: #721c24;
          border: 1px solid #f5c6cb;
        }

        .typing-indicator {
          display: flex;
          align-items: center;
        }

        .typing-indicator span {
          height: 8px;
          width: 8px;
          border-radius: 50%;
          background: #999;
          margin: 0 2px;
          animation: typing 1.4s infinite ease-in-out;
        }

        .typing-indicator span:nth-child(1) {
          animation-delay: -0.32s;
        }

        .typing-indicator span:nth-child(2) {
          animation-delay: -0.16s;
        }

        @keyframes typing {
          0%, 80%, 100% {
            transform: scale(0.8);
            opacity: 0.5;
          }
          40% {
            transform: scale(1);
            opacity: 1;
          }
        }

        .rating-buttons {
          display: flex;
          gap: 4px;
        }

        .query-result {
          font-size: 0.85rem;
          border: 1px solid #dee2e6;
        }

        .sql-query pre {
          font-size: 0.75rem;
          max-height: 100px;
          overflow-y: auto;
        }
      `}</style>
    </Modal>
  );
}

export default ChatBot;
