/**
 * Login Page - KHO MVG
 * Trang ƒëƒÉng nh·∫≠p v·ªõi validation v√† security features
 */

import React, { useState, useEffect } from 'react';
import { Form, Button, Alert, Container, Card } from 'react-bootstrap';
import { useAuth } from '../../contexts/AuthContext';
import { useNotification } from '../../contexts/NotificationContext';

function Login() {
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [rememberMe, setRememberMe] = useState(false);

  const { login } = useAuth();
  const { showError, showSuccess } = useNotification();

  // Load saved credentials if remember me was checked
  useEffect(() => {
    const savedUsername = localStorage.getItem('remembered_username');
    if (savedUsername) {
      setFormData(prev => ({ ...prev, username: savedUsername }));
      setRememberMe(true);
    }
  }, []);

  /**
   * Handle form input changes
   */
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error when user starts typing
    if (error) {
      setError('');
    }
  };

  /**
   * Handle form submission
   */
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Basic validation
    if (!formData.username.trim()) {
      setError('Vui l√≤ng nh·∫≠p t√™n ƒëƒÉng nh·∫≠p ho·∫∑c email');
      return;
    }

    if (!formData.password) {
      setError('Vui l√≤ng nh·∫≠p m·∫≠t kh·∫©u');
      return;
    }

    if (formData.password.length < 6) {
      setError('M·∫≠t kh·∫©u ph·∫£i c√≥ √≠t nh·∫•t 6 k√Ω t·ª±');
      return;
    }

    setLoading(true);

    try {
      console.log('üîç Login attempt:', { username: formData.username, hasPassword: !!formData.password });
      const result = await login(formData);
      console.log('üîç Login result:', result);
      
      if (!result.success) {
        throw new Error(result.message || 'ƒêƒÉng nh·∫≠p th·∫•t b·∫°i');
      }
      
      // Save username if remember me is checked
      if (rememberMe) {
        localStorage.setItem('remembered_username', formData.username);
      } else {
        localStorage.removeItem('remembered_username');
      }

      showSuccess('ƒêƒÉng nh·∫≠p th√†nh c√¥ng!');
    } catch (error) {
      import('../../utils/errorLogger').then(({ default: ErrorLogger }) => {
        ErrorLogger.logError(error, { action: 'login', username: formData.username });
      });
      setError(error.message || 'ƒêƒÉng nh·∫≠p th·∫•t b·∫°i');
      showError(error.message || 'ƒêƒÉng nh·∫≠p th·∫•t b·∫°i');
    } finally {
      setLoading(false);
    }
  };

  /**
   * Handle demo login (for testing)
   */
  const handleDemoLogin = async (role = 'admin') => {
    const demoCredentials = {
      admin: { username: 'admin', password: 'admin123' },
      manager: { username: 'manager', password: 'manager123' },
      staff: { username: 'staff', password: 'staff123' }
    };

    const credentials = demoCredentials[role];
    if (!credentials) return;

    setFormData(credentials);
    setLoading(true);

    try {
      await login(credentials.username, credentials.password);
      showSuccess(`ƒêƒÉng nh·∫≠p demo th√†nh c√¥ng v·ªõi role ${role}!`);
    } catch (error) {
      import('../../utils/errorLogger').then(({ default: ErrorLogger }) => {
        ErrorLogger.logError(error, { action: 'demoLogin' });
      });
      showError('Demo login th·∫•t b·∫°i');
    } finally {
      setLoading(false);
    }
  };

  /**
   * Check if form is valid
   */
  const isFormValid = formData.username.trim() && formData.password.length >= 6;

  return (
    <Container fluid className="login-container">
      <Card className="login-card">
        <div className="login-header">
          <div className="login-logo">
            <i className="fas fa-warehouse"></i>
          </div>
          <h2 className="login-title">KHO MVG</h2>
          <p className="login-subtitle">
            H·ªá th·ªëng qu·∫£n l√Ω h·ªó tr·ª£ kinh doanh c√°c d·ª± √°n kho x∆∞·ªüng
          </p>
        </div>

        <Form onSubmit={handleSubmit}>
          {error && (
            <Alert variant="danger" className="mb-3">
              <i className="fas fa-exclamation-circle me-2"></i>
              {error}
            </Alert>
          )}

          <Form.Group className="mb-3">
            <Form.Label>
              <i className="fas fa-user me-2"></i>
              T√™n ƒëƒÉng nh·∫≠p ho·∫∑c Email
            </Form.Label>
            <Form.Control
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Nh·∫≠p t√™n ƒëƒÉng nh·∫≠p ho·∫∑c email"
              disabled={loading}
              autoComplete="username"
              autoFocus
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>
              <i className="fas fa-lock me-2"></i>
              M·∫≠t kh·∫©u
            </Form.Label>
            <Form.Control
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Nh·∫≠p m·∫≠t kh·∫©u"
              disabled={loading}
              autoComplete="current-password"
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Check
              type="checkbox"
              label="Ghi nh·ªõ ƒëƒÉng nh·∫≠p"
              checked={rememberMe}
              onChange={(e) => setRememberMe(e.target.checked)}
              disabled={loading}
            />
          </Form.Group>

          <Button
            type="submit"
            variant="primary"
            size="lg"
            className="w-100 mb-3"
            disabled={!isFormValid || loading}
          >
            {loading ? (
              <>
                <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                ƒêang ƒëƒÉng nh·∫≠p...
              </>
            ) : (
              <>
                <i className="fas fa-sign-in-alt me-2"></i>
                ƒêƒÉng nh·∫≠p
              </>
            )}
          </Button>

          {/* Demo login buttons for development */}
          {process.env.NODE_ENV === 'development' && (
            <div className="demo-login-section">
              <hr className="my-3" />
              <small className="text-muted d-block text-center mb-2">Demo Login (Development)</small>
              <div className="d-grid gap-2">
                <Button
                  variant="outline-primary"
                  size="sm"
                  onClick={() => handleDemoLogin('admin')}
                  disabled={loading}
                >
                  Demo Admin
                </Button>
                <div className="row g-1">
                  <div className="col">
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      className="w-100"
                      onClick={() => handleDemoLogin('manager')}
                      disabled={loading}
                    >
                      Demo Manager
                    </Button>
                  </div>
                  <div className="col">
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      className="w-100"
                      onClick={() => handleDemoLogin('staff')}
                      disabled={loading}
                    >
                      Demo Staff
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </Form>

        <div className="text-center mt-4">
          <small className="text-muted">
            B·∫±ng c√°ch ƒëƒÉng nh·∫≠p, b·∫°n ƒë·ªìng √Ω v·ªõi{' '}
            <a href="/terms" className="text-decoration-none">
              ƒêi·ªÅu kho·∫£n s·ª≠ d·ª•ng
            </a>{' '}
            v√†{' '}
            <a href="/privacy" className="text-decoration-none">
              Ch√≠nh s√°ch b·∫£o m·∫≠t
            </a>
          </small>
        </div>

        <div className="text-center mt-3">
          <small className="text-muted">
            <i className="fas fa-shield-alt me-1"></i>
            K·∫øt n·ªëi b·∫£o m·∫≠t SSL
          </small>
        </div>
      </Card>

      {/* Background elements for visual appeal */}
      <div style={{
        position: 'absolute',
        top: '10%',
        left: '10%',
        width: '100px',
        height: '100px',
        background: 'rgba(255,255,255,0.1)',
        borderRadius: '50%',
        animation: 'float 6s ease-in-out infinite'
      }}></div>
      
      <div style={{
        position: 'absolute',
        bottom: '15%',
        right: '15%',
        width: '150px',
        height: '150px',
        background: 'rgba(255,255,255,0.05)',
        borderRadius: '50%',
        animation: 'float 8s ease-in-out infinite reverse'
      }}></div>

      <style jsx>{`
        @keyframes float {
          0%, 100% {
            transform: translateY(0px);
          }
          50% {
            transform: translateY(-20px);
          }
        }

        .demo-login-section {
          border-top: 1px solid #e9ecef;
          padding-top: 1rem;
        }

        .login-card {
          position: relative;
          z-index: 2;
        }

        @media (max-width: 576px) {
          .login-card {
            margin: 1rem;
            padding: 1.5rem;
          }
          
          .login-logo {
            font-size: 2.5rem;
          }
          
          .login-title {
            font-size: 1.5rem;
          }
        }
      `}</style>
    </Container>
  );
}

export default Login;
